
public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Single Assignment
		int a = 10;
		System.out.println(a);
		
		// Multiple Assignment
		int x, y, z;
		x = y = z = 100;
		System.out.println(x+" "+y+" "+z);
		
		// Complex Assignment
		int u = 100;
		u += a;
		System.out.println(u);
	}

}
