
public class ComparisionOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a1 = 100;
		int a2 = 200;
		
		System.out.println(a1 < a2);
		System.out.println(a1 > a2);
		System.out.println(a1 <= a2);
		System.out.println(a1 >= a2);
		System.out.println(a1 == a2);
		System.out.println(a1 != a2);
		
		// Object comparision is also -> Done using ==, !=
		
		
	}

}
