
public class AccessSpecifier {
	
	
	// public
	public int a = 10;
	public void display1() {
		System.out.println("Public method");
	}
	
	// Protected 
	protected int b = 10;
	protected void display2() {
		System.out.println("Public method");
	}
	
	// default 
	int c = 10;
	void display3() {
		System.out.println("Public method");
	}
	
	// private 
	private	int d = 10;
	private	void display4() {
		System.out.println("Public method");
	}
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

	}

}
